mapper.select:
		select b.*, nvl(l.likecount, 0) as likecount 
		from board b
        left join (
            select board_num, count(*) as likecount
            from likes
            group by board_num
        ) l
        on b.board_num = l.board_num


                AND category = #{category}


                AND price >= #{minPrice}


                AND price <= #{maxPrice}


                AND board_type = #{board_type}


        order by board_date desc

		select b.*, nvl(l.likecount, 0) as likecount 
		from board b
        left join (
            select board_num, count(*) as likecount
            from likes
            group by board_num
        ) l
        on b.board_num = l.board_num


                AND category = #{category}


                AND price >= #{minPrice}


                AND price <= #{maxPrice}


                AND board_type = #{board_type}


        order by view_count desc, likecount desc
